<?phpinclude 'conexion.php';include 'sqlite.php';setlocale(LC_TIME, "es_CO");$fecha=strftime("%A, %d de %B de %Y");    function resolucion(){        $consulta=mysql_query('select id from resolucion');        $array=mysql_fetch_array($consulta);        $numero=$array['id']+1;        return  $numero;    }    function nits(){        $consulta=mysql_query('SELECT * FROM empresa');        while( $array=mysql_fetch_array($consulta)){            echo '<option value="'.$array['nit'].'">'.$array['nit'].' | '.$array['nombre'].'</option>';        }    }     function dias( ){        $dias = cal_days_in_month(CAL_GREGORIAN, date("n"), date("Y"));        return $dias;    }    function selectDias(){        for( $dias=dias();$dias>0;$dias--)        {            echo '<option value="'.$dias.'"> '.$dias.' </option>';        }    }function meses(){    $meses = array('Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre');    foreach($meses as $variable){        echo '<option value="'.$variable.'">'.$variable.'</option>';    }}function anos(){    $ano=date('Y');    for($ano; $ano>1965; $ano--){        echo '<option value="'.$ano.'">'.$ano.'</option>';    }}function empresas(){    $consulta=mysql_query('SELECT nombre FROM empresa');    while( $array=mysql_fetch_array($consulta)){       echo '<option value="'.$array['nombre'].'">'.$array['nombre'].'</option>';    }}$r=new myDB();?><!DOCTYPE html><html><head>	<title></title>    <script src="../js/jquery-1.8.2.min.js" type="text/javascript"></script>    <link rel="stylesheet" type="text/css" href="../css/formato.css">    <script src="../ckeditor/ckeditor.js"></script>    <link href="../css/bootstrap.css" rel="stylesheet" type="text/css">    <script type="text/javascript" src="../js/bootstrap.js"></script>    <script src="../js/add_empresa.js"></script>    <script src="../js/resolucion.js"